mapper.select:

SELECT
	RNO,BNO,REPLY,REPLYER,REPLYDATE,UPDATEDATE 
FROM(
    SELECT /*+ INDEX(TBL_REPLY IDX_REPLY) */
    	ROWNUM RN,RNO,BNO,REPLY,REPLYER,REPLYDATE,UPDATEDATE
    FROM
        TBL_REPLY
    WHERE BNO=#{bno}
    AND RNO>0
    AND ROWNUM<=#{paging.pageNum}*#{paging.amount}
    )
WHERE
	RN>(#{paging.pageNum}-1)*#{paging.amount}


SELECT COUNT(RNO)
FROM TBL_REPLY
WHERE BNO=#{BNO}
